package tema2;

public class operaciones {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double A=30;
	    double B=60;
	    
		System.out.println("A=30");
		System.out.println("B=60");
		
		System.out.println("A elevado a B");
		System.out.println(Math.pow(A,B));
		System.out.println("raiz A");
		System.out.println(Math.sqrt(A));
		System.out.println("cos A");
		System.out.println(Math.cos(A));
		System.out.println("sen A");
		System.out.println(Math.sin(A));
		System.out.println("tg A");
		System.out.println(Math.tan(A));
		System.out.println("cotg A");
		System.out.println(Math.atan2(A,B));

	}

}
